mapper.insert:
INSERT INTO todolist
SET  content = #{content},
     user_id = #{userId}

mapper.select:
SELECT is_completed AS isCompleted
FROM todolist
WHERE list_id = #{listId}

mapper.update:
UPDATE  todolist
SET  is_completed = 1
WHERE list_id = #{listId}

UPDATE  todolist
SET  is_completed = 0
WHERE list_id = #{listId}
